import React,{useState} from 'react';
import './Style.css'
import { connect } from 'react-redux';
import {RemoveContact,EditContact,UpdateContact} from '../redux/ContactActions'
import AddContactModal from './AddContactModal'
import { MDBDataTable } from 'mdbreact';



const ContactTable = (props) => {
    const {data,handleEdit,handleDelete}=props
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);

    const data1 = {
        columns: [
          {
            label: 'S No',
            field: 'ID',
            sort: 'asc',
            width:70
          },
          {
            label: 'FirstName',
            field: 'FirstName',
            sort: 'asc',
            width: 200
          },
          {
            label: 'Last Name',
            field: 'LastName',
            sort: 'asc',
            width: 150
          },
          {
            label: 'Mobile',
            field: 'Mobile',
            sort: 'asc',
            width: 100
          },
          {
            label: 'Email',
            field: 'EmailID',
            sort: 'asc',
            width: 100
          },
          {
            label: 'Start date',
            field: 'date',
            sort: 'asc',
            width: 150
          },
          {
            label: 'Salary',
            field: 'salary',
            sort: 'asc',
            width: 100
          },
          {
            label: 'Extn.',
            field: 'extn',
            sort: 'asc',
            width: 100
          },
          {
            label: "E-mail",
            field: 'email',
            sort: 'asc',
            width: 200
          }
        ],
        rows: [
          {
            name: 'Tiger',
            surname: 'Nixon',
            position: 'System Architect',
            office: 'Edinburgh',
            age: '61',
            date: '2011/04/25',
            salary: '$320,800',
            extn: 5421,
            email: 't.nixon@datatables.net'
          },
          {
            name: 'Garrett',
            surname: 'Winters',
            position: 'Accountant',
            office: 'Tokyo',
            age: '63',
            date: '2011/07/25',
            salary: '$170,750',
            extn: 8422,
            email: 'q.winters@datatables.net'
          },
          {
            name: 'Ashton',
            surname: 'Cox',
            position: 'Junior Technical Author',
            office: 'San Francisco',
            age: '66',
            date: '2009/01/12',
            salary: '$86,000',
            extn: 1562,
            email: 'a.cox@datatables.net'
          },
          {
            name: 'Cedric',
            surname: 'Kelly',
            position: 'Senior Javascript Developer',
            office: 'Edinburgh',
            age: '22',
            date: '2012/03/29',
            salary: '$433,060',
            extn: 6224,
            email: 'c.kelly@datatables.net'
          },
          {
            name: 'Airi',
            surname: 'Satou',
            position: 'Accountant',
            office: 'Tokyo',
            age: '33',
            date: '2008/11/28',
            salary: '$162,700',
            extn: 5407,
            email: 'a.satou@datatables.net'
          },
          {
            name: 'Brielle',
            surname: 'Williamson',
            position: 'Integration Specialist',
            office: 'New York',
            age: '61',
            date: '2012/12/02',
            salary: '$372,000',
            extn: 4804,
            email: 'b.williamson@datatables.net'
          },
        ]
      };
    
    
    const handleSubmit = (data) => {
        setShow(false)
        props.handleUpdate(data)
        
    };


    return (
        <div className="col-sm-12">
            <MDBDataTable
                scrollX
                scrollY
                maxHeight="300px"
                striped
                bordered
                data={data1}
            />
        <table className="table table-sm table-striped header-fixed">
            <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">FirstName</th>
                    <th scope="col">Last Name</th>
                    <th scope="col">Mobile</th>
                    <th scope="col">Email</th>
                    <th scope="col">{" "}</th>
                    <th scope="col">{" "}</th>

                </tr>
            </thead>
            <tbody >
                {
                    data!==undefined&&data.map((item)=>(
                        <tr key={item.ID}>
                            <td scope="col">{item.ID}</td>
                            <td scope="col">{item.FirstName}</td>
                            <td scope="col">{item.LastName}</td>
                            <td scope="col">{item.Mobile}</td>
                            <td scope="col">{item.EmailID}</td>
                            <td scope="col"><button onClick={()=>{
                                setShow(true)
                                handleEdit(item)
                            }}  className="btn btn-danger">Edit</button></td>
                            <td scope="col"><button onClick={()=>{handleDelete(item.ID)}}  className="btn btn-danger">Remove</button></td>

                        </tr>
                    ))
                }
               
            </tbody>
        </table>
        <AddContactModal title={"Update Contact"} show={show} handleClose={handleClose} handleSubmit={handleSubmit}/>       
    </div>  
    );
};


const mapDispatchToProps=(dispatch)=>{
    return{
        handleEdit:(data)=>{dispatch(EditContact(data))},
        handleDelete:(id)=>{dispatch(RemoveContact(id))},
        handleUpdate:(data)=>{dispatch(UpdateContact(data))}

    }
}
export default connect(null,mapDispatchToProps)(ContactTable);
